  return cart.length ? (
    // return (
    <div className="link-container">
      {cart.map((product) => (
        <CartItem key={product} product={product} removeItemFromCart={removeItem}
        />
      ))}
          
    </div>
      
    
  ) : (
    <div>
      No hay art√≠culos en tu carrito
      <Link to="/page2"><button>
              Go to Page 2 
            </button>
            </Link>
    </div>
  );
};

export default Cart;
