  return cart.length ? (
    // return (
    <div className="link-container">
      {cart.map((product) => (
        <CartItem key={product} product={product} removeItemFromCart={removeItem}
        />
      ))}
          <Link to="/" className="link">
          Volver
            </Link>
    </div>
      
    
  ) : (
    <div>No hay art√≠culos en tu carrito</div>
  );
};

export default Cart;
