import { Link } from 'react-router-dom';
import Button from '@material-ui/core/Button';


const CartItem = ({ product, removeItemFromCart }) => {
    return (
      <div>
        <div className="card">
          <div className="media">
            <img src={product.url} alt={product.name} />
          </div>
          <div className="card-content">
            <div>
              <h3>{product.name}</h3>
              <p>{product.price}â‚¬</p>
              <button onClick={() => removeItemFromCart(product.name)}>
                Borrar producto
              </button>
            </div>
          </div>
        </div>
        <Link to="/">
        <Button className="allProducts"  variant="contained" color="primary" disableElevation>
        Volver a Inicio
        </Button>
      </Link>
      </div>
    );
  };
  
  export default CartItem;
