import React, {useEffect,useState} from "react";
import axios from 'axios';
import './style.css';
import Navbar from "./components/Navbar";
import Products from "./components/Products";
import Cart from "./components/Cart";
import Footer from "./components/Footer"
import AppBar from "./components/AppBar"
import Button from '@material-ui/core/Button';
import Pagination from './components/Pagination';

import { BrowserRouter as Router, Switch, Route } from "react-router-dom";


export default function App() {
  const [products, setProducts] = useState([]);
  const [cart, setCart] = useState(false);
  const [currentPage,setCurrentPage] = useState(1);
  const [productsPerPage,setProductsPerPage] = useState(10);

  useEffect(() => {
    async function fetchMyAPI(){
      let response = await axios.get('/products')
      console.log(response.data)

      setProducts(response.data)
      setCart(false)

    }

    if(cart === true){
      fetchMyAPI()
    }

  }, [cart])

  console.log(products)//aquí tengo todos los productos



  /*************PAGINACIÓN QUE NO SALEEEEEEEEEEEEEEEEEE */
//para obtener los productos actuales por página
  const indexOfLastProduct = currentPage * productsPerPage;
  const indexOfFirstProduct = indexOfLastProduct - productsPerPage;
  const currentProducts = products.slice(indexOfFirstProduct,indexOfLastProduct);

   // Change page
   const paginate = pageNumber => setCurrentPage(pageNumber);
//pagination
console.log(`$products`)
function Pagination({ products, RenderComponent, title, pageLimit, productsLimit }) {
  const [pages] = useState(Math.round(products.length / productsLimit));
  const [currentPage, setCurrentPage] = useState(1);

  function goToNextPage() {
      setCurrentPage((page) => page + 1);
  }

  function goToPreviousPage() {
      setCurrentPage((page) => page - 1);
  }

  function changePage(event) {
      const pageNumber = Number(event.target.textContent);
      setCurrentPage(pageNumber);
  }

  const getPaginatedData = () => {
      const startIndex = currentPage * productsLimit - productsLimit;
      const endIndex = startIndex + productsLimit;
      return products.slice(startIndex, endIndex);
  };

  const getPaginationGroup = () => {
      let start = Math.floor((currentPage - 1) / pageLimit) * pageLimit;
      return new Array(pageLimit).fill().map((_, idx) => start + idx + 1);
  };

  return (
      <div>
      <h1>{title}</h1>

      {/* show the posts, 10 posts at a time */}
      <div className="dataContainer">
        {getPaginatedData().map((d, idx) => (
          <RenderComponent key={idx} products={d} />
        ))}
      </div>

      {/* show the pagiantion
          it consists of next and previous buttons
          along with page numbers, in our case, 5 page
          numbers at a time
      */}
      <div className="pagination">
        {/* previous button */}
        <button
          onClick={goToPreviousPage}
          className={`prev ${currentPage === 1 ? 'disabled' : ''}`}
        >
          prev
        </button>

        {/* show page numbers */}
        {getPaginationGroup().map((item, index) => (
          <button
            key={index}
            onClick={changePage}
            className={`paginationItem ${currentPage === item ? 'active' : null}`}
          >
            <span>{item}</span>
          </button>
        ))}

        {/* next button */}
        <button
          onClick={goToNextPage}
          className={`next ${currentPage === pages ? 'disabled' : ''}`}
        >
          next
        </button>
      </div>
    </div>
  );

}

  const handleAddToCart = (title, price, image, id) => {
    setCart([...cart, { title, price, image, id }]);
  };

  const handleRemoveItem = (id) => {
    setCart(cart.filter((item) => item.id !== id));
    console.log(id);
  };
  const handleClick = () => setCart(true);
  return (
    <Router>
      <div className= "AppBar">
        <AppBar></AppBar>
      </div>
      <div className="App">

        <Navbar cart={cart} />       
        <Switch>
          <Route exact path="/">
            <Products products={products} addToCart={handleAddToCart} />
          </Route>         
          <Route exact path="/cart">
            <Cart cart={cart} removeItem={handleRemoveItem} />
          </Route>
        </Switch>
        <Button onClick={handleClick} variant="contained" color="primary" disableElevation>
  Ver todos los productos
        </Button>
        <Products products={currentProducts}/>
        <>
          <Pagination
            data={products}
            RenderComponent={Products}
            title="Products"
            pageLimit={5}
            dataLimit={10}
          />
        </>
      </div>
      <div className="footer"><Footer></Footer></div>

    </Router>
  );
}
