           <React.Fragment>
                <div>
                    <SearchBar ItemValue={FilterString} onChangeFilter={HandleFilter}/>
                    {loading?<div style={{marginTop:'15vh'}}><Loading/></div>:(
                    <div>
                            <div className="row m-1">
                            {
                                FilterItems.map((product)=>(
                                    <Product key={product._id} product={product}/>
                                ))
                            }
                        </div>
                    <Pagination/>
                    </div>
                  
                    )}
                </div>
           </React.Fragment>
    )
}
