import React, {useState,useEffect,useContext} from 'react';
import { useRedirect } from '../../Hooks/useRedirect';
import ProductContext from '../../context/ProductContext';
import './ProductDetail.css'
import axios from 'axios';



const ProductDetail = () => {
    const [productDetail, setProductDetail] = useState("");
    const redirect = useRedirect();

    const ProductDetailCxt = useContext(ProductContext);
    console.log("ProductID",ProductDetailCxt.id);

    const fetchMyBase = async() => {
        let response = await axios('/products')
        console.log(response.data)

        setProductDetail({
            "name": response.data.name,
            "brand":response.data.brand,
            "image":response.data.image,
            "price":response.data.price,
            "description":response.data.description,
            "producer":response.data.producer,
            "address":response.data.address,
            "cif":response.data.cif
        })
    }




    /* const fetchData = async() =>{
        const url = `http://localhost:8888/productDetails/?id=${ProductDetailCxt.id}`
            fetch (url)
                .then(response => response.json())
                .then(data => {
                    if(data){
                        setProductDetail({
                            "name": data.name,
                            "brand":data.brand,
                            "image":data.image,
                            "price":data.price,
                            "description":data.description,
                            "producer":data.producer,
                            "address":data.address,
                            "cif":data.cif
                        })
                        console.log("productDetail:", data)
                    }
                })
    } */

    useEffect(() => {
       fetchMyBase()

    }, [])

    return(
        <div id="DetailContainer">

            <div>
            <img id="back" src="{back}" alt="goBack" onClick={(e)=>(redirect("/"),e)}></img>
            </div>

            <div id="ProductImg">
                <div id="circle2"></div>
                <img id="productimg" src={productDetail.image} alt="producto"></img> 
            </div>

            <div id="ProductInfo">
                <h2>{productDetail.brand}</h2>
                <h3>{productDetail.name}</h3>
                <p>{productDetail.price}</p>
                <p>{productDetail.description}</p>
            </div>

            <div id="Maker">
                <h3>{productDetail.producer}</h3>
                <p>{productDetail.address}</p>
                <p>{productDetail.cif}</p>
            </div>
        </div>
    )

}
export default ProductDetail;
